int scr_dialogue_exit()
{
    if (dialogue_switch_mode == 2) {
        return -1;
    }

    if (gdialog_state == 0) {
        return 0;
    }

    gdialog_free_speech();
    gdReviewFree();
    remove_bk_process(head_bk);

    if (PID_TYPE(dialog_target->pid) != OBJ_TYPE_ITEM) {
        if (gdPlayerTile != obj_dude->tile) {
            gdCenterTile = obj_dude->tile;
        }
        tile_scroll_to(gdCenterTile, 2);
    }

    talk_to_destroy_head_window();

    text_font(oldFont);

    if (fidgetFp != NULL) {
        art_ptr_unlock(fidgetKey);
        fidgetFp = NULL;
    }

    if (lipsKey != NULL) {
        if (art_ptr_unlock(lipsKey) == -1) {
            debug_printf("Failure unlocking lips frame!\n");
        }
        lipsKey = NULL;
        lipsFp = NULL;
        lipsFID = 0;
    }

    // NOTE: Uninline.
    talk_to_blend_table_exit();

    gdialog_state = 0;
    dialogue_state = 0;

    cycle_enable();

    gmouse_enable_scrolling();

    if (gDialogMusicVol == -1) {
        gsound_background_restart_last(11);
    } else {
        gsound_background_volume_set(gDialogMusicVol);
    }

    if (boxesWereDisabled) {
        enable_box_bar_win();
    }

    boxesWereDisabled = 0;

    if (gdDialogTurnMouseOff) {
        gmouse_enable();

        gdDialogTurnMouseOff = 0;
    }

    gmouse_3d_on();

    gdDialogWentOff = true;

    return 0;
}

int gsound_init()
{
    if (gsound_initialized) {
        if (gsound_debug) {
            debug_printf("Trying to initialize gsound twice.\n");
        }
        return -1;
    }

    bool initialize;
    configGetBool(&game_config, GAME_CONFIG_SOUND_KEY, GAME_CONFIG_INITIALIZE_KEY, &initialize);
    if (!initialize) {
        return 0;
    }

    configGetBool(&game_config, GAME_CONFIG_SOUND_KEY, GAME_CONFIG_DEBUG_KEY, &gsound_debug);

    if (gsound_debug) {
        debug_printf("Initializing sound system...");
    }

    if (gsound_get_music_path(&sound_music_path1, GAME_CONFIG_MUSIC_PATH1_KEY) != 0) {
        return -1;
    }

    if (gsound_get_music_path(&sound_music_path2, GAME_CONFIG_MUSIC_PATH2_KEY) != 0) {
        return -1;
    }

    if (strlen(sound_music_path1) > 247 || strlen(sound_music_path2) > 247) {
        if (gsound_debug) {
            debug_printf("Music paths way too long.\n");
        }
        return -1;
    }

    // gsound_setup_paths
    if (gsound_setup_paths() != 0) {
        return -1;
    }

    soundRegisterAlloc(mem_malloc, mem_realloc, mem_free);

    // initialize direct sound
    if (soundInit(detectDevices, 24, 0x8000, 0x8000, 22050) != 0) {
        if (gsound_debug) {
            debug_printf("failed!\n");
        }

        return -1;
    }

    if (gsound_debug) {
        debug_printf("success.\n");
    }

    initAudiof(gsound_compressed_query);
    initAudio(gsound_compressed_query);

    int cacheSize;
    config_get_value(&game_config, GAME_CONFIG_SOUND_KEY, GAME_CONFIG_CACHE_SIZE_KEY, &cacheSize);
    if (cacheSize >= 0x40000) {
        debug_printf("\n!!! Config file needs adustment.  Please remove the ");
        debug_printf("cache_size line and run fallout again.  This will reset ");
        debug_printf("cache_size to the new default, which is expressed in K.\n");
        return -1;
    }

    if (sfxc_init(cacheSize << 10, sound_sfx_path) != 0) {
        if (gsound_debug) {
            debug_printf("Unable to initialize sound effects cache.\n");
        }
    }

    if (soundSetDefaultFileIO(gsound_open, gsound_close, gsound_read, gsound_write, gsound_seek, gsound_tell, gsound_filesize) != 0) {
        if (gsound_debug) {
            debug_printf("Failure setting sound I/O calls.\n");
        }
        return -1;
    }

    add_bk_process(gsound_bkg_proc);
    gsound_initialized = true;

    // SOUNDS
    bool sounds = 0;
    configGetBool(&game_config, GAME_CONFIG_SOUND_KEY, GAME_CONFIG_SOUNDS_KEY, &sounds);

    if (gsound_debug) {
        debug_printf("Sounds are ");
    }

    if (sounds) {
        // NOTE: Uninline.
        gsound_sfx_enable();
    } else {
        if (gsound_debug) {
            debug_printf(" not ");
        }
    }

    if (gsound_debug) {
        debug_printf("on.\n");
    }

    // MUSIC
    bool music = 0;
    configGetBool(&game_config, GAME_CONFIG_SOUND_KEY, GAME_CONFIG_MUSIC_KEY, &music);

    if (gsound_debug) {
        debug_printf("Music is ");
    }

    if (music) {
        // NOTE: Uninline.
        gsound_background_enable();
    } else {
        if (gsound_debug) {
            debug_printf(" not ");
        }
    }

    if (gsound_debug) {
        debug_printf("on.\n");
    }

    // SPEEECH
    bool speech = 0;
    configGetBool(&game_config, GAME_CONFIG_SOUND_KEY, GAME_CONFIG_SPEECH_KEY, &speech);

    if (gsound_debug) {
        debug_printf("Speech is ");
    }

    if (speech) {
        // NOTE: Uninline.
        gsound_speech_enable();
    } else {
        if (gsound_debug) {
            debug_printf(" not ");
        }
    }

    if (gsound_debug) {
        debug_printf("on.\n");
    }

    config_get_value(&game_config, GAME_CONFIG_SOUND_KEY, GAME_CONFIG_MASTER_VOLUME_KEY, &master_volume);
    gsound_set_master_volume(master_volume);

    config_get_value(&game_config, GAME_CONFIG_SOUND_KEY, GAME_CONFIG_MUSIC_VOLUME_KEY, &background_volume);
    gsound_background_volume_set(background_volume);

    config_get_value(&game_config, GAME_CONFIG_SOUND_KEY, GAME_CONFIG_SNDFX_VOLUME_KEY, &sndfx_volume);
    gsound_set_sfx_volume(sndfx_volume);

    config_get_value(&game_config, GAME_CONFIG_SOUND_KEY, GAME_CONFIG_SPEECH_VOLUME_KEY, &speech_volume);
    gsound_speech_volume_set(speech_volume);

    // NOTE: Uninline.
    gsound_background_fade_set(0);
    background_fname_requested[0] = '\0';

    return 0;
}

int gsound_background_volume_get_set(int volume)
{
    int oldMusicVolume;

    // NOTE: Uninline.
    oldMusicVolume = gsound_background_volume_get();
    gsound_background_volume_set(volume);

    return oldMusicVolume;
}

static void DoThing(int eventCode)
{
    int x;
    int y;
    mouseGetPositionInWindow(prfwin, &x, &y);

    // This preference index also contains out-of-bounds value 19,
    // which is the only preference expressed as checkbox.
    int preferenceIndex = eventCode - 505;

    if (preferenceIndex >= FIRST_PRIMARY_PREF && preferenceIndex <= LAST_PRIMARY_PREF) {
        PreferenceDescription* meta = &(btndat[preferenceIndex]);
        int* valuePtr = meta->valuePtr;
        int value = *valuePtr;
        bool valueChanged = false;

        int v1 = meta->knobX + 23;
        int v2 = meta->knobY + 21;

        if (sqrt(pow((double)x - (double)v1, 2) + pow((double)y - (double)v2, 2)) > 16.0) {
            if (y > meta->knobY) {
                int v14 = meta->knobY + bglby[0];
                if (y >= v14 && y <= v14 + text_height()) {
                    if (x >= meta->minX && x <= meta->knobX) {
                        *valuePtr = 0;
                        meta->direction = 0;
                        valueChanged = true;
                    } else {
                        if (meta->valuesCount >= 3 && x >= meta->knobX + bglbx[2] && x <= meta->maxX) {
                            *valuePtr = 2;
                            meta->direction = 0;
                            valueChanged = true;
                        }
                    }
                }
            } else {
                if (x >= meta->knobX + 9 && x <= meta->knobX + 37) {
                    *valuePtr = 1;
                    if (value != 0) {
                        meta->direction = 1;
                    } else {
                        meta->direction = 0;
                    }
                    valueChanged = true;
                }
            }

            if (meta->valuesCount == 4) {
                int v19 = meta->knobY + bglby[3];
                if (y >= v19 && y <= v19 + 2 * text_height() && x >= meta->knobX + bglbx[3] && x <= meta->maxX) {
                    *valuePtr = 3;
                    meta->direction = 1;
                    valueChanged = true;
                }
            }
        } else {
            if (meta->direction != 0) {
                if (value == 0) {
                    meta->direction = 0;
                }
            } else {
                if (value == meta->valuesCount - 1) {
                    meta->direction = 1;
                }
            }

            if (meta->direction != 0) {
                *valuePtr = value - 1;
            } else {
                *valuePtr = value + 1;
            }

            valueChanged = true;
        }

        if (valueChanged) {
            gsound_play_sfx_file("ib3p1xx1");
            block_for_tocks(70);
            gsound_play_sfx_file("ib3lu1x1");
            UpdateThing(preferenceIndex);
            win_draw(prfwin);
            changed = true;
            return;
        }
    } else if (preferenceIndex >= FIRST_SECONDARY_PREF && preferenceIndex <= LAST_SECONDARY_PREF) {
        PreferenceDescription* meta = &(btndat[preferenceIndex]);
        int* valuePtr = meta->valuePtr;
        int value = *valuePtr;
        bool valueChanged = false;

        int v1 = meta->knobX + 11;
        int v2 = meta->knobY + 12;

        if (sqrt(pow((double)x - (double)v1, 2) + pow((double)y - (double)v2, 2)) > 10.0) {
            int v23 = meta->knobY - 5;
            if (y >= v23 && y <= v23 + text_height() + 2) {
                if (x >= meta->minX && x <= meta->knobX) {
                    *valuePtr = preferenceIndex == PREF_COMBAT_MESSAGES ? 1 : 0;
                    valueChanged = true;
                } else if (x >= meta->knobX + 22.0 && x <= meta->maxX) {
                    *valuePtr = preferenceIndex == PREF_COMBAT_MESSAGES ? 0 : 1;
                    valueChanged = true;
                }
            }
        } else {
            *valuePtr ^= 1;
            valueChanged = true;
        }

        if (valueChanged) {
            gsound_play_sfx_file("ib2p1xx1");
            block_for_tocks(70);
            gsound_play_sfx_file("ib2lu1x1");
            UpdateThing(preferenceIndex);
            win_draw(prfwin);
            changed = true;
            return;
        }
    } else if (preferenceIndex >= FIRST_RANGE_PREF && preferenceIndex <= LAST_RANGE_PREF) {
        PreferenceDescription* meta = &(btndat[preferenceIndex]);
        int* valuePtr = meta->valuePtr;

        gsound_play_sfx_file("ib1p1xx1");

        double value;
        switch (preferenceIndex) {
        case PREF_TEXT_BASE_DELAY:
            value = 6.0 - text_delay + 1.0;
            break;
        case PREF_BRIGHTNESS:
            value = gamma_value;
            break;
        case PREF_MOUSE_SENSITIVIY:
            value = mouse_sens;
            break;
        default:
            value = *valuePtr;
            break;
        }

        int knobX = (int)(219.0 / (meta->maxValue - meta->minValue));
        int v31 = (int)((value - meta->minValue) * (219.0 / (meta->maxValue - meta->minValue)) + 384.0);
        buf_to_buf(prfbmp[PREFERENCES_WINDOW_FRM_BACKGROUND] + PREFERENCES_WINDOW_WIDTH * meta->knobY + 384, 240, 12, PREFERENCES_WINDOW_WIDTH, prefbuf + PREFERENCES_WINDOW_WIDTH * meta->knobY + 384, PREFERENCES_WINDOW_WIDTH);
        trans_buf_to_buf(prfbmp[PREFERENCES_WINDOW_FRM_KNOB_ON], 21, 12, 21, prefbuf + PREFERENCES_WINDOW_WIDTH * meta->knobY + v31, PREFERENCES_WINDOW_WIDTH);

        win_draw(prfwin);

        int sfxVolumeExample = 0;
        int speechVolumeExample = 0;
        while (true) {
            sharedFpsLimiter.mark();

            get_input();

            int tick = get_time();

            mouseGetPositionInWindow(prfwin, &x, &y);

            if (mouse_get_buttons() & 0x10) {
                gsound_play_sfx_file("ib1lu1x1");
                UpdateThing(preferenceIndex);
                win_draw(prfwin);
                renderPresent();
                changed = true;
                return;
            }

            if (v31 + 14 > x) {
                if (v31 + 6 > x) {
                    v31 = x - 6;
                    if (v31 < 384) {
                        v31 = 384;
                    }
                }
            } else {
                v31 = x - 6;
                if (v31 > 603) {
                    v31 = 603;
                }
            }

            double newValue = ((double)v31 - 384.0) / (219.0 / (meta->maxValue - meta->minValue)) + meta->minValue;

            int v52 = 0;

            switch (preferenceIndex) {
            case PREF_COMBAT_SPEED:
                *meta->valuePtr = (int)newValue;
                break;
            case PREF_TEXT_BASE_DELAY:
                text_delay = 6.0 - newValue + 1.0;
                break;
            case PREF_MASTER_VOLUME:
                *meta->valuePtr = (int)newValue;
                gsound_set_master_volume(master_volume);
                v52 = 1;
                break;
            case PREF_MUSIC_VOLUME:
                *meta->valuePtr = (int)newValue;
                gsound_background_volume_set(music_volume);
                v52 = 1;
                break;
            case PREF_SFX_VOLUME:
                *meta->valuePtr = (int)newValue;
                gsound_set_sfx_volume(sndfx_volume);
                v52 = 1;
                if (sfxVolumeExample == 0) {
                    gsound_play_sfx_file("butin1");
                    sfxVolumeExample = 7;
                } else {
                    sfxVolumeExample--;
                }
                break;
            case PREF_SPEECH_VOLUME:
                *meta->valuePtr = (int)newValue;
                gsound_speech_volume_set(speech_volume);
                v52 = 1;
                if (speechVolumeExample == 0) {
                    gsound_speech_play("narrator\\options", 12, 13, 15);
                    speechVolumeExample = 40;
                } else {
                    speechVolumeExample--;
                }
                break;
            case PREF_BRIGHTNESS:
                gamma_value = newValue;
                colorGamma(newValue);
                break;
            case PREF_MOUSE_SENSITIVIY:
                mouse_sens = newValue;
                break;
            }

            if (v52) {
                int off = PREFERENCES_WINDOW_WIDTH * (meta->knobY - 12) + 384;
                buf_to_buf(prfbmp[PREFERENCES_WINDOW_FRM_BACKGROUND] + off, 240, 24, PREFERENCES_WINDOW_WIDTH, prefbuf + off, PREFERENCES_WINDOW_WIDTH);

                for (int optionIndex = 0; optionIndex < meta->valuesCount; optionIndex++) {
                    const char* str = getmsg(&optn_msgfl, &optnmesg, meta->labelIds[optionIndex]);

                    int x;
                    switch (optionIndex) {
                    case 0:
                        // 0x4926AA
                        x = 384;
                        // TODO: Incomplete.
                        break;
                    case 1:
                        // 0x4926F3
                        switch (meta->valuesCount) {
                        case 2:
                            x = 624 - text_width(str);
                            break;
                        case 3:
                            // This code path does not use floating-point arithmetic
                            x = 504 - text_width(str) / 2 - 2;
                            break;
                        case 4:
                            // Uses floating-point arithmetic
                            x = 444 + text_width(str) / 2 - 8;
                            break;
                        }
                        break;
                    case 2:
                        // 0x492766
                        switch (meta->valuesCount) {
                        case 3:
                            x = 624 - text_width(str);
                            break;
                        case 4:
                            // Uses floating-point arithmetic
                            x = 564 - text_width(str) - 4;
                            break;
                        }
                        break;
                    case 3:
                        // 0x49279E
                        x = 624 - text_width(str);
                        break;
                    }
                    text_to_buf(prefbuf + PREFERENCES_WINDOW_WIDTH * (meta->knobY - 12) + x, str, PREFERENCES_WINDOW_WIDTH, PREFERENCES_WINDOW_WIDTH, colorTable[18979]);
                }
            } else {
                int off = PREFERENCES_WINDOW_WIDTH * meta->knobY + 384;
                buf_to_buf(prfbmp[PREFERENCES_WINDOW_FRM_BACKGROUND] + off, 240, 12, PREFERENCES_WINDOW_WIDTH, prefbuf + off, PREFERENCES_WINDOW_WIDTH);
            }

            trans_buf_to_buf(prfbmp[PREFERENCES_WINDOW_FRM_KNOB_ON], 21, 12, 21, prefbuf + PREFERENCES_WINDOW_WIDTH * meta->knobY + v31, PREFERENCES_WINDOW_WIDTH);
            win_draw(prfwin);

            while (elapsed_time(tick) < 35) {
            }

            renderPresent();
            sharedFpsLimiter.throttle();
        }
    } else if (preferenceIndex == 19) {
        player_speedup ^= 1;
    }

    changed = true;
}

static void UpdateThing(int index)
{
    text_font(101);

    PreferenceDescription* meta = &(btndat[index]);

    if (index >= FIRST_PRIMARY_PREF && index <= LAST_PRIMARY_PREF) {
        // 0x4812FC
        static const int offsets[PRIMARY_PREF_COUNT] = {
            66, // game difficulty
            143, // combat difficulty
            222, // violence level
            304, // target highlight
            382, // combat looks
        };

        int primaryOptionIndex = index - FIRST_PRIMARY_PREF;

        if (primaryOptionIndex == PREF_RUNNING_BURNING_GUY && !game_global_vars[GVAR_RUNNING_BURNING_GUY]) {
            return;
        }

        buf_to_buf(prfbmp[PREFERENCES_WINDOW_FRM_BACKGROUND] + 640 * offsets[primaryOptionIndex] + 23, 160, 54, 640, prefbuf + 640 * offsets[primaryOptionIndex] + 23, 640);

        for (int valueIndex = 0; valueIndex < meta->valuesCount; valueIndex++) {
            const char* text = getmsg(&optn_msgfl, &optnmesg, meta->labelIds[valueIndex]);

            char copy[100]; // TODO: Size is probably wrong.
            strcpy(copy, text);

            int x = meta->knobX + bglbx[valueIndex];
            int len = text_width(copy);
            switch (valueIndex) {
            case 0:
                x -= text_width(copy);
                meta->minX = x;
                break;
            case 1:
                x -= len / 2;
                meta->maxX = x + len;
                break;
            case 2:
            case 3:
                meta->maxX = x + len;
                break;
            }

            char* p = copy;
            while (*p != '\0' && *p != ' ') {
                p++;
            }

            int y = meta->knobY + bglby[valueIndex];
            const char* s;
            if (*p != '\0') {
                *p = '\0';
                text_to_buf(prefbuf + 640 * y + x, copy, 640, 640, colorTable[18979]);
                s = p + 1;
                y += text_height();
            } else {
                s = copy;
            }

            text_to_buf(prefbuf + 640 * y + x, s, 640, 640, colorTable[18979]);
        }

        int value = *(meta->valuePtr);
        trans_buf_to_buf(prfbmp[PREFERENCES_WINDOW_FRM_PRIMARY_SWITCH] + (46 * 47) * value, 46, 47, 46, prefbuf + 640 * meta->knobY + meta->knobX, 640);
    } else if (index >= FIRST_SECONDARY_PREF && index <= LAST_SECONDARY_PREF) {
        // 0x481310
        static const int offsets[SECONDARY_PREF_COUNT] = {
            66, // combat messages
            133, // combat taunts
            200, // language filter
            264, // running
            331, // subtitles
            397, // item highlight
        };

        int secondaryOptionIndex = index - FIRST_SECONDARY_PREF;

        buf_to_buf(prfbmp[PREFERENCES_WINDOW_FRM_BACKGROUND] + 640 * offsets[secondaryOptionIndex] + 251, 113, 34, 640, prefbuf + 640 * offsets[secondaryOptionIndex] + 251, 640);

        // Secondary options are booleans, so it's index is also it's value.
        for (int value = 0; value < 2; value++) {
            const char* text = getmsg(&optn_msgfl, &optnmesg, meta->labelIds[value]);

            int x;
            if (value) {
                x = meta->knobX + smlbx[value];
                meta->maxX = x + text_width(text);
            } else {
                x = meta->knobX + smlbx[value] - text_width(text);
                meta->minX = x;
            }
            text_to_buf(prefbuf + 640 * (meta->knobY - 5) + x, text, 640, 640, colorTable[18979]);
        }

        int value = *(meta->valuePtr);
        if (index == PREF_COMBAT_MESSAGES) {
            value ^= 1;
        }
        trans_buf_to_buf(prfbmp[PREFERENCES_WINDOW_FRM_SECONDARY_SWITCH] + (22 * 25) * value, 22, 25, 22, prefbuf + 640 * meta->knobY + meta->knobX, 640);
    } else if (index >= FIRST_RANGE_PREF && index <= LAST_RANGE_PREF) {
        buf_to_buf(prfbmp[PREFERENCES_WINDOW_FRM_BACKGROUND] + 640 * (meta->knobY - 12) + 384, 240, 24, 640, prefbuf + 640 * (meta->knobY - 12) + 384, 640);
        switch (index) {
        case PREF_COMBAT_SPEED:
            if (1) {
                double value = *meta->valuePtr;
                value = my_clamp(value, 0.0, 50.0);

                int x = (int)((value - meta->minValue) * 219.0 / (meta->maxValue - meta->minValue) + 384.0);
                trans_buf_to_buf(prfbmp[PREFERENCES_WINDOW_FRM_KNOB_OFF], 21, 12, 21, prefbuf + 640 * meta->knobY + x, 640);
            }
            break;
        case PREF_TEXT_BASE_DELAY:
            if (1) {
                text_delay = my_clamp(text_delay, 1.0, 6.0);

                int x = (int)((6.0 - text_delay) * 43.8 + 384.0);
                trans_buf_to_buf(prfbmp[PREFERENCES_WINDOW_FRM_KNOB_OFF], 21, 12, 21, prefbuf + 640 * meta->knobY + x, 640);

                double value = (text_delay - 1.0) * 0.2 * 2.0;
                value = my_clamp(value, 0.0, 2.0);

                text_object_set_base_delay(text_delay);
                text_object_set_line_delay(value);
            }
            break;
        case PREF_MASTER_VOLUME:
        case PREF_MUSIC_VOLUME:
        case PREF_SFX_VOLUME:
        case PREF_SPEECH_VOLUME:
            if (1) {
                double value = *meta->valuePtr;
                value = my_clamp(value, meta->minValue, meta->maxValue);

                int x = (int)((value - meta->minValue) * 219.0 / (meta->maxValue - meta->minValue) + 384.0);
                trans_buf_to_buf(prfbmp[PREFERENCES_WINDOW_FRM_KNOB_OFF], 21, 12, 21, prefbuf + 640 * meta->knobY + x, 640);

                switch (index) {
                case PREF_MASTER_VOLUME:
                    gsound_set_master_volume(master_volume);
                    break;
                case PREF_MUSIC_VOLUME:
                    gsound_background_volume_set(music_volume);
                    break;
                case PREF_SFX_VOLUME:
                    gsound_set_sfx_volume(sndfx_volume);
                    break;
                case PREF_SPEECH_VOLUME:
                    gsound_speech_volume_set(speech_volume);
                    break;
                }
            }
            break;
        case PREF_BRIGHTNESS:
            if (1) {
                gamma_value = my_clamp(gamma_value, 1.0, 1.17999267578125);

                int x = (int)((gamma_value - meta->minValue) * (219.0 / (meta->maxValue - meta->minValue)) + 384.0);
                trans_buf_to_buf(prfbmp[PREFERENCES_WINDOW_FRM_KNOB_OFF], 21, 12, 21, prefbuf + 640 * meta->knobY + x, 640);

                colorGamma(gamma_value);
            }
            break;
        case PREF_MOUSE_SENSITIVIY:
            if (1) {
                mouse_sens = my_clamp(mouse_sens, 1.0, 2.5);

                int x = (int)((mouse_sens - meta->minValue) * (219.0 / (meta->maxValue - meta->minValue)) + 384.0);
                trans_buf_to_buf(prfbmp[PREFERENCES_WINDOW_FRM_KNOB_OFF], 21, 12, 21, prefbuf + 640 * meta->knobY + x, 640);

                mouse_set_sensitivity(mouse_sens);
            }
            break;
        }

        for (int optionIndex = 0; optionIndex < meta->valuesCount; optionIndex++) {
            const char* str = getmsg(&optn_msgfl, &optnmesg, meta->labelIds[optionIndex]);

            int x;
            switch (optionIndex) {
            case 0:
                // 0x4926AA
                x = 384;
                // TODO: Incomplete.
                break;
            case 1:
                // 0x4926F3
                switch (meta->valuesCount) {
                case 2:
                    x = 624 - text_width(str);
                    break;
                case 3:
                    // This code path does not use floating-point arithmetic
                    x = 504 - text_width(str) / 2 - 2;
                    break;
                case 4:
                    // Uses floating-point arithmetic
                    x = 444 + text_width(str) / 2 - 8;
                    break;
                }
                break;
            case 2:
                // 0x492766
                switch (meta->valuesCount) {
                case 3:
                    x = 624 - text_width(str);
                    break;
                case 4:
                    // Uses floating-point arithmetic
                    x = 564 - text_width(str) - 4;
                    break;
                }
                break;
            case 3:
                // 0x49279E
                x = 624 - text_width(str);
                break;
            }
            text_to_buf(prefbuf + 640 * (meta->knobY - 12) + x, str, 640, 640, colorTable[18979]);
        }
    } else {
        // return false;
    }

    // TODO: Incomplete.

    // return true;
}

static void JustUpdate()
{
    game_difficulty = my_clamp(game_difficulty, 0, 2);
    combat_difficulty = my_clamp(combat_difficulty, 0, 2);
    violence_level = my_clamp(violence_level, 0, 3);
    target_highlight = my_clamp(target_highlight, 0, 2);
    combat_messages = my_clamp(combat_messages, 0, 1);
    running_burning_guy = my_clamp(running_burning_guy, 0, 1);
    combat_taunts = my_clamp(combat_taunts, 0, 1);
    language_filter = my_clamp(language_filter, 0, 1);
    prf_running = my_clamp(prf_running, 0, 1);
    subtitles = my_clamp(subtitles, 0, 1);
    item_highlight = my_clamp(item_highlight, 0, 1);
    combat_speed = my_clamp(combat_speed, 0, 50);
    player_speedup = my_clamp(player_speedup, 0, 1);
    text_delay = my_clamp(text_delay, 1.0, 6.0);
    master_volume = my_clamp(master_volume, 0, VOLUME_MAX);
    music_volume = my_clamp(music_volume, 0, VOLUME_MAX);
    sndfx_volume = my_clamp(sndfx_volume, 0, VOLUME_MAX);
    speech_volume = my_clamp(speech_volume, 0, VOLUME_MAX);
    gamma_value = my_clamp(gamma_value, 1.0, 1.17999267578125);
    mouse_sens = my_clamp(mouse_sens, 1.0, 2.5);

    text_object_set_base_delay(text_delay);
    gmouse_3d_synch_item_highlight();

    double textLineDelay = (text_delay + (-1.0)) * 0.2 * 2.0;
    textLineDelay = my_clamp(textLineDelay, 0.0, 2.0);

    text_object_set_line_delay(textLineDelay);
    combatai_refresh_messages();
    scr_message_free();
    gsound_set_master_volume(master_volume);
    gsound_background_volume_set(music_volume);
    gsound_set_sfx_volume(sndfx_volume);
    gsound_speech_volume_set(speech_volume);
    mouse_set_sensitivity(mouse_sens);
    colorGamma(gamma_value);
}
